INSERT INTO `wp_posts` VALUES("100", "1", "2017-07-20 17:11:31", "2017-07-20 14:11:31", "[fusion_builder_container hundred_percent=\"no\" equal_height_columns=\"no\" menu_anchor=\"\" hide_on_mobile=\"small-visibility,medium-visibility,large-visibility\" class=\"\" id=\"\" background_color=\"\" background_image=\"\" background_position=\"center center\" background_repeat=\"no-repeat\" fade=\"no\" background_parallax=\"none\" enable_mobile=\"no\" parallax_speed=\"0.3\" video_mp4=\"\" video_webm=\"\" video_ogv=\"\" video_url=\"\" video_aspect_ratio=\"16:9\" video_loop=\"yes\" video_mute=\"yes\" video_preview_image=\"\" border_size=\"\" border_color=\"\" border_style=\"solid\" margin_top=\"\" margin_bottom=\"\" padding_top=\"\" padding_right=\"\" padding_bottom=\"\" padding_left=\"\"][fusion_builder_row][fusion_builder_column type=\"1_1\" spacing=\"\" center_content=\"no\" hover_type=\"none\" link=\"\" min_height=\"\" hide_on_mobile=\"small-visibility,medium-visibility,large-visibility\" class=\"\" id=\"\" background_color=\"\" background_image=\"\" background_position=\"left top\" background_repeat=\"no-repeat\" border_size=\"0\" border_color=\"\" border_style=\"solid\" border_position=\"all\" padding=\"\" dimension_margin=\"undefined\" animation_type=\"\" animation_direction=\"left\" animation_speed=\"0.3\" animation_offset=\"\" last=\"no\"][fusion_text]<div class=\"page-title\">\r\n        <h1>Back to School</h1>\r\n    </div>\r\n<div class=\"std\"><h2><span style=\"font-size: 12px;\">Let us reduce the stress of \"Back to School\" Time.</span></h2>\r\n<div class=\"BlockContent\">\r\n<p>&nbsp;</p>\r\n<p><strong><span>Bulk Deliveries<br></span></strong>Orders will be delivered to your school for distribution to your students. Cartons will be clearly labelled with a class or grade as required.<br>Mesh bags can be supplied by us.<br><strong><span>Payment:</span></strong>&nbsp;School will be invoiced.</p>\r\n<p><strong><span><br></span></strong></p>\r\n<p><strong><span>Sale Days<br></span></strong>Our expert staff will set up at your school and distribute directly to your students.<br><strong><span>Payment<strong>:</strong></span></strong>&nbsp;Using on our online ordering and payment facility, all sale days school have designated packs for each respective school year, just follow the prompts to order and pay and its done! This applies to all credit card payments (VISA/Mastercard, sorry Amex is not accepted) Payment on the day of your schools allocated Sale day can still be made with Cash - exact amount.</p>\r\n<p>&nbsp;</p>\r\n<p><strong><span>Grade/Class Packs<br></span></strong>All students receive the same pack designed by your teachers. These will be individually packed and delivered to your school for the start of Term 1.<br><strong><span>Payment:</span></strong>&nbsp;School will be invoiced.</p>\r\n<p>&nbsp;</p>\r\n<p><strong><span>On-line Grade/Class Packs<br></span></strong>All students receive the same pack designed by your teachers. These will be individually packed and delivered to your school.<br>Parents/Carers order on-line at www.ljharper.com.au and follow links to your school booklist.<br><strong><span>Payment:</span></strong>&nbsp;Parents/Carers pay on-line using our secure payment gateway.</p>\r\n<p><strong><span><br></span></strong></p>\r\n<p><strong><span>Student Packs</span></strong><br>100% customisable, so you have the freedom to create any combination of products you consider suitable for each individual grade or class at your school.</p>\r\n<p>&nbsp;Products include:</p>\r\n<p>- Student Workbooks<br>- Exercise Books<br>- Display Folders, Manilla Folders, Binders<br>- Pens, Pencils, Crayons, Textas, Highlighters, Markers<br>- Rulers, Sharpeners, Maths Tools, Stencils, Glue Sticks<br>- Earphones, USB Sticks, Computer Accessories<br>&nbsp;</p>\r\n</div></div>[/fusion_text][/fusion_builder_column][/fusion_builder_row][/fusion_builder_container]", "BTS", "", "publish", "closed", "closed", "", "bts", "", "", "2017-07-20 17:11:31", "2017-07-20 14:11:31", "", "0", "http://topbookshop.nodramas.net.au/?page_id=100", "0", "page", "", "0");
